package tw.org.iii;

public class java20 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int c = 0 ; c < 2 ; c ++){
			for (int b = 1 ; b<=9 ; b++){
				for (int a = 2 ; a <= 5 ; a++){ 
					int newi = a + c * 4 ;
				System.out.print(newi + "*" + b + "=" + newi * b + "\t");
		}
		System.out.println();
		
		}
		System.out.println();
		}
	}
}
